import React, { useState } from "react";
import axios from "axios";
import './App.css';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';

function App() {
    const [gameName, setGameName] = useState("");
    const [tagLine, setTagLine] = useState("");
    const [region, setRegion] = useState("na1"); // Default region
    const [playerStats, setPlayerStats] = useState(null); // Initial state
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const regions = [
        { value: "na1", label: "North America" },
        { value: "euw1", label: "Europe West" },
        { value: "eun1", label: "Europe Nordic & East" },
        { value: "kr", label: "Korea" },
        { value: "jp1", label: "Japan" },
        { value: "br1", label: "Brazil" },
        { value: "la1", label: "Latin America North" },
        { value: "la2", label: "Latin America South" },
        { value: "oc1", label: "Oceania" },
        { value: "tr1", label: "Turkey" },
        { value: "ru", label: "Russia" },
        { value: "ph2", label: "Philippines" },
        { value: "sg2", label: "Singapore" },
        { value: "th2", label: "Thailand" },
        { value: "tw2", label: "Taiwan" },
        { value: "vn2", label: "Vietnam" },
    ];

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!gameName.trim() || !tagLine.trim()) {
            setError("Please enter both Game Name and Tag Line");
            return;
        }
        setLoading(true);
        setError(null);
        setPlayerStats(null); 

        try {
            const response = await axios.get(`http://localhost:3001/api/player/${gameName.trim()}/${tagLine.trim()}/${region}/stats`);
            setPlayerStats(response.data);
        } catch (err) {
            console.error('Error fetching player stats:', err);
            if (err.response && err.response.data && err.response.data.error) {
                setError(err.response.data.error);
            } else if (err.message) {
                setError(err.message);
            } else {
                setError("An error occurred while fetching player stats.");
            }
        } finally {
            setLoading(false);
        }
    };

    const getPieChartData = (wins, losses) => {
        const data = [];
        if (wins > 0) data.push({ name: 'Wins', value: wins });
        if (losses > 0) data.push({ name: 'Losses', value: losses });
        if (wins === 0 && losses === 0 && data.length === 0) {
            data.push({ name: 'N/A', value: 1 });
        }
        return data;
    };


    return (
        <div className="App">
            <header className={`App-header ${playerStats ? 'hide-header' : ''}`}>
                <h1>LoL Stat Tracker</h1>
            </header>
            <main>
                <form onSubmit={handleSubmit} className={`search-form ${playerStats ? 'search-form-top' : ''}`}>
                    <div className="input-group">
                        <input
                            type="text"
                            value={gameName}
                            onChange={(e) => setGameName(e.target.value)}
                            placeholder="Enter Riot Game Name"
                            required
                        />
                        <span className="tagline-separator">#</span>
                        <input
                            type="text"
                            value={tagLine}
                            onChange={(e) => setTagLine(e.target.value)}
                            placeholder="TAG"
                            required
                            className="tagline-input"
                        />
                    </div>
                    <select value={region} onChange={(e) => setRegion(e.target.value)}>
                        {regions.map((r) => (
                            <option key={r.value} value={r.value}>
                                {r.label} ({r.value.toUpperCase()})
                            </option>
                        ))}
                    </select>
                    <button type="submit" disabled={loading}>
                        {loading ? 'Searching...' : 'Get Stats'}
                    </button>
                </form>

                {error && <p className="error-message">{error}</p>}

                {/* Conditional Rendering for the entire stats block */}
                {playerStats && (
                    <div className="stats-container">
                        <h2>
                            Match Stats for {gameName}#{tagLine} ({region.toUpperCase()})
                        </h2>

                        {/* Display message from backend if any (e.g., "No matches found") */}
                        {playerStats.message && <p>{playerStats.message}</p>}

                        {/* Overall Win Rate Chart - Check if overallWins/Losses exist */}
                        {(typeof playerStats.overallWins === 'number' && typeof playerStats.overallLosses === 'number') && (
                            <div className="charts-section">
                                <h3>Overall Win Rate ({playerStats.overallWins + playerStats.overallLosses} Games)</h3>
                                <p>Wins: {playerStats.overallWins} | Losses: {playerStats.overallLosses}</p>
                                <ResponsiveContainer width="100%" height={250}>
                                    <PieChart>
                                        <Pie
                                            dataKey="value"
                                            data={getPieChartData(playerStats.overallWins, playerStats.overallLosses)}
                                            outerRadius={80}
                                            labelLine={false}
                                            label={({ name, percent, value }) => (value && name !== 'N/A') ? `${name}: ${(percent * 100).toFixed(0)}%` : (name === 'N/A' ? 'No Games' : '')}
                                        >
                                            {/* Conditional Cells */}
                                            {getPieChartData(playerStats.overallWins, playerStats.overallLosses).map((entry, index) => (
                                                <Cell key={`cell-${index}`} fill={entry.name === 'Wins' ? '#28a745' : entry.name === 'Losses' ? '#dc3545' : '#cccccc'} />
                                            ))}
                                        </Pie>
                                        <Tooltip />
                                        <Legend />
                                    </PieChart>
                                </ResponsiveContainer>

                                {/* Champion Win Rates - Check if championWinRates is an array and has items */}
                                {playerStats.championWinRates && playerStats.championWinRates.length > 0 ? (
                                    playerStats.championWinRates.map((champ, index) => {
                                        const wins = champ.wins;
                                        const losses = champ.gamesPlayed - champ.wins;
                                        return (
                                            <div key={index} style={{ marginTop: '2rem' }}>
                                                <h4>{champ.championName} ({champ.gamesPlayed} Games)</h4>
                                                <p>Wins: {wins} | Losses: {losses}</p>
                                                <ResponsiveContainer width="100%" height={200}>
                                                    <PieChart>
                                                        <Pie
                                                            dataKey="value"
                                                            data={getPieChartData(wins, losses)}
                                                            outerRadius={70}
                                                            labelLine={false}
                                                            label={({ name, percent, value }) => (value && name !== 'N/A') ? `${name}: ${(percent * 100).toFixed(0)}%` : (name === 'N/A' ? 'No Games' : '')}
                                                        >
                                                            {getPieChartData(wins, losses).map((entry, index) => (
                                                                <Cell key={`cell-champ-${index}`} fill={entry.name === 'Wins' ? '#28a745' : entry.name === 'Losses' ? '#dc3545' : '#cccccc'} />
                                                            ))}
                                                        </Pie>
                                                        <Tooltip />
                                                        <Legend />
                                                    </PieChart>
                                                </ResponsiveContainer>
                                            </div>
                                        );
                                    })
                                ) : (
                                    // Only show "No specific champion data" if championWinRates is an empty array (and not undefined)
                                    playerStats.championWinRates && <p>No specific champion data to display for the top champions.</p>
                                )}
                            </div>
                        )}

                        {/* Recent Matches - Check if matches is an array and has items */}
                        {playerStats.matches && playerStats.matches.length > 0 ? (
                            <>
                                <h4>Recent Matches:</h4>
                                <ul className="match-list">
                                    {playerStats.matches.map((match) => (
                                        <li key={match.match_id} className={match.win ? 'match-win' : 'match-loss'}>
                                            <div className="match-info">
                                                <span className="champion-name">{match.championName || 'N/A'}</span>
                                                <span className="game-mode">({match.gameMode ? match.gameMode.replace(/_/g, ' ') : 'N/A'})</span>
                                            </div>
                                            <div className="match-score">
                                                KDA: {match.kills}/{match.deaths}/{match.assists}
                                            </div>
                                            <div className="match-result">
                                                {match.win ? 'Victory' : 'Defeat'}
                                            </div>
                                            <div className="match-timestamp">
                                                {new Date(match.gameCreation).toLocaleDateString()}{' '}
                                                {new Date(match.gameCreation).toLocaleTimeString()}
                                            </div>
                                        </li>
                                    ))}
                                </ul>
                            </>
                        ) : (
                            playerStats.matches && !playerStats.message && <p>No recent matches to display.</p>
                        )}

                        <button className="home-btn" onClick={() => {
                            setPlayerStats(null);
                            setGameName(""); 
                            setTagLine("");
                            setError(null);
                        }}>
                            Search Another Player
                        </button>
                    </div>
                )}
            </main>
        </div>
    );
}
