import React,{useState} from "react";
import axios from "axios";
import './App.css';
import {PieChart, Pie, Cell, ResponsiveContainer,Tooltip,Legend} from 'recharts';

function App() {
  const [gameName, setGameName] = useState("");
  const [tagLine, setTagLine] = useState("");
  const [region, setRegion] = useState("na1");
  const [playerStats, setPlayerStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const regions = [
    { value: "na1", label: "North America" },
    { value: "euw1", label: "Europe West" },
    { value: "eun1", label: "Europe Nordic & East" },
    { value: "kr", label: "Korea" },
    { value: "jp1", label: "Japan" },
    { value: "br1", label: "Brazil" },
    { value: "la1", label: "Latin America North" },
    { value: "la2", label: "Latin America South" },
    { value: "oc1", label: "Oceania" },
  ]

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!gameName || !tagLine.trim()) {
      setError("Please enter both Game Name and Tag Line");
      return;
    }
    setLoading(true);
    setError('');
    setPlayerStats(null);

    try {
      const response = await axios.get(`http://localhost:3001/api/player/${gameName.trim()}/${tagLine.trim()}/${region}/stats`);
        setPlayerStats(response.data);
      } catch (err) {
        console.error('Error fetching player stats:', err);
        if (err.response && err.response.data && err.response.data.error) {
          setError(err.response.data.error);
        } else if (err.message) {
          setError(err.message);
        }
        else {
          setError("An error occurred while fetching player stats.");
        }
      }
      finally {
        setLoading(false);
      }
  };

 return (
        <div className="App">
            <header className="App-header" style={{display: playerStats ? 'none' : 'block'}}>
                <h1>LoL Stat Tracker</h1>
            </header>
            <main>
                <form onSubmit={handleSubmit} className={`search-form ${playerStats ? 'search-form-top' : ''}`}>
                    <div className="input-group">
                        <input
                            type="text"
                            value={gameName}
                            onChange={(e) => setGameName(e.target.value)}
                            placeholder="Enter Riot Game Name (e.g., PlayerName)"
                            required
                        />
                        <span className="tagline-separator">#</span>
                        <input
                            type="text"
                            value={tagLine}
                            onChange={(e) => setTagLine(e.target.value)}
                            placeholder="TAG (e.g., 5012)"
                            required
                            className="tagline-input"
                        />
                    </div>
                    <select value={region} onChange={(e) => setRegion(e.target.value)}>
                        {regions.map(r => (
                            <option key={r.value} value={r.value}>{r.label} ({r.value.toUpperCase()})</option>
                        ))}
                    </select>
                    <button type="submit" disabled={loading}>
                        {loading ? 'Searching...' : 'Get Stats'}
                    </button>
                </form>

                {error && <p className="error-message">{error}</p>}

                {playerStats && (
                    <div className="stats-container">
                        <h2>Match Stats for {gameName}#{tagLine} ({region.toUpperCase()})</h2>
                        {playerStats.message && <p>{playerStats.message}</p>}

                        {playerStats.matches && playerStats.matches.length > 0 && (
                            <>
                                {playerStats && (
                                <div className="charts-section">
                                    <h3>Overall Win Rate</h3>
                                    <ResponsiveContainer width="100%" height={250}>
                                    <PieChart>
                                        <Pie dataKey="value" data={[
                                        { name: "Wins", value: playerStats.overallWins },
                                        { name: "Losses", value: playerStats.overallLosses }
                                        ]} outerRadius={80} fill="#8884d8" label>
                                        <Cell fill="#28a745" />
                                        <Cell fill="#dc3545" />
                                        </Pie>
                                        <Tooltip />
                                        <Legend />
                                    </PieChart>
                                    </ResponsiveContainer>
                                </div>
                                )}

                                {playerStats.championWinRates && playerStats.championWinRates.length > 0 && (
                                    <div className="champion-stats">
                                        <h4>Top Champion Win Rates:</h4>
                                        <ul>
                                            {playerStats.championWinRates.map(champ => {
                                                const losses = champ.gamesPlayed - champ.wins;
                                                return (
                                                    <div key={champ.championName} className="champion-chart">
                                                    <h4>{champ.championName}</h4>
                                                    <ResponsiveContainer width="100%" height={200}>
                                                        <PieChart>
                                                        <Pie dataKey="value" data={[
                                                            { name: "Wins", value: champ.wins },
                                                            { name: "Losses", value: losses }
                                                        ]} outerRadius={70} label>
                                                            <Cell fill="#28a745" />
                                                            <Cell fill="#dc3545" />
                                                        </Pie>
                                                        <Tooltip />
                                                        <Legend />
                                                        </PieChart>
                                                    </ResponsiveContainer>
                                                    </div>
                                                );
                                                })}

                                        </ul>
                                    </div>
                                )}

                                <h4>Recent Matches:</h4>
                                <ul className="match-list">
                                    {playerStats.matches.map(match => (
                                        <li key={match.matchId} className={match.win ? 'match-win' : 'match-loss'}>
                                            <div className="match-info">
                                                <span className="champion-name">{match.championName}</span>
                                                <span className="game-mode">({match.gameMode.replace(/_/g, ' ')})</span>
                                            </div>
                                            <div className="match-score">
                                                KDA: {match.kills}/{match.deaths}/{match.assists}
                                            </div>
                                            <div className="match-result">
                                                {match.win ? 'Victory' : 'Defeat'}
                                            </div>
                                            <div className="match-timestamp">
                                                {new Date(match.gameCreation).toLocaleDateString()} {new Date(match.gameCreation).toLocaleTimeString()}
                                            </div>
                                        </li>
                                    ))}
                                </ul>
                            </>
                        )}
                    </div>
                )}
            </main>
        </div>
    );
}

export default App;
